<?php
add_action('after_setup_theme', 'adelante_siempre_setup'); function adelante_siempre_setup() { add_theme_support('post-thumbnails'); register_nav_menus(array('main-menu' => 'Main Menu')); } function adelante_add_editor_styles() { add_editor_style( 'custom-editor-style.css' ); } add_action( 'admin_init', 'adelante_add_editor_styles' ); add_action('init', 'cpt_banner'); function cpt_banner() { register_post_type( 'banner', array( 'labels' => array( 'name' => __( 'Banners' ), 'singular_name' => __( 'Banner' ) ), 'supports' => array('title', 'thumbnail'), 'public' => true, 'menu_icon' => 'dashicons-images-alt' ) ); } add_action('init', 'cpt_artes'); function cpt_artes() { register_post_type( 'artes', array( 'labels' => array( 'name' => __( 'Artes' ), 'singular_name' => __( 'Arte' ) ), 'supports' => array('title', 'thumbnail', 'editor'), 'public' => true, 'publicly_queryable' => true, 'menu_icon' => 'dashicons-images-alt' ) ); } add_action('wp_enqueue_scripts', 'adelante_siempre_load_scripts'); function adelante_siempre_load_scripts() { wp_enqueue_style('dashicons'); wp_enqueue_style('GoogleFonts', "https://fonts.googleapis.com/css2?family=Roboto:ital,wght@0,300;0,700;1,300;1,700&display=swap"); if (is_front_page()) { wp_enqueue_style('swiper', 'https://unpkg.com/swiper@6.1.1/swiper-bundle.min.css'); wp_enqueue_style('liteYt', get_template_directory_uri() . '/css/lite-yt-embed.css'); } wp_enqueue_style('adelante_siempre-style', get_stylesheet_uri(), array(), '2020.08.11'); } add_action('wp_footer', 'adelante_siempre_footer_scripts'); function adelante_siempre_footer_scripts() { if (is_front_page()) { wp_enqueue_script( 'swiper', 'https://unpkg.com/swiper@6.1.1/swiper-bundle.min.js'); wp_enqueue_script( 'liteYt', get_template_directory_uri() . '/scripts/lite-yt-embed.js'); wp_enqueue_script( 'scripts', get_template_directory_uri() . '/scripts/scripts.js', array('swiper', 'liteYt'), '2020.08.11' ); } else { wp_enqueue_script( 'scripts', get_template_directory_uri() . '/scripts/single.js', array(), '2020.08.11' ); wp_enqueue_script( 'featherIcons', 'https://cdnjs.cloudflare.com/ajax/libs/feather-icons/4.28.0/feather.min.js'); wp_enqueue_script( 'clipboardCopy', 'https://cdnjs.cloudflare.com/ajax/libs/clipboard-polyfill/3.0.1/promise/clipboard-polyfill.promise.min.js'); } wp_enqueue_script( 'headerListener', get_template_directory_uri() . '/scripts/header-listener.js', array(), '2020.08.11' ); } add_filter('document_title_separator', 'adelante_siempre_document_title_separator'); function adelante_siempre_document_title_separator($sep) { $sep = '|'; return $sep; } ?>